mapper.select:
select

  distinct


from search_sciencedirect_by_publication

  where ${whereClause}


  order by ${orderByClause}

select

  distinct


from search_record

  where ${whereClause}


  order by ${orderByClause}

mapper.update:
    update search_sciencedirect_by_publication
	set have_read = 1
    where id = #{id,jdbcType=INTEGER}

    update search_record
	set parsed = 1
    where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert ignore into search_sciencedirect_by_publication ( TITLE, ABSTRACT, KEYWORD, CORRESPONDINGAUTHOR,CORRESPONDINGADDRESS,AUTHORS,ADDRESSES,
	EMAIL,SOURCE_PUBLICATION,PDATE,reference,full_text,url
  )
values (#{title,jdbcType=VARCHAR}, #{abs,jdbcType=LONGVARCHAR}, #{keyword,jdbcType=VARCHAR}, #{correspondingAuthor,jdbcType=LONGVARCHAR}, #{correspondingAddress,jdbcType=LONGVARCHAR}, 
  #{authors,jdbcType=LONGVARCHAR}, #{addresses,jdbcType=LONGVARCHAR},#{email,jdbcType=VARCHAR},#{sourcePublication,jdbcType=VARCHAR},
  #{pDate,jdbcType=VARCHAR},#{reference,jdbcType=LONGVARCHAR},#{fullText,jdbcType=LONGVARCHAR},
  #{url,jdbcType=VARCHAR}
  )

insert ignore into search_sciencedirect_by_keyword ( TITLE, ABSTRACT, KEYWORD, CORRESPONDINGAUTHOR,CORRESPONDINGADDRESS,AUTHORS,ADDRESSES,
	EMAIL,SOURCE_PUBLICATION,PDATE,reference,full_text,url,searchKeyword
  )
values (#{title,jdbcType=VARCHAR}, #{abs,jdbcType=LONGVARCHAR}, #{keyword,jdbcType=VARCHAR}, #{correspondingAuthor,jdbcType=LONGVARCHAR}, #{correspondingAddress,jdbcType=LONGVARCHAR}, 
  #{authors,jdbcType=LONGVARCHAR}, #{addresses,jdbcType=LONGVARCHAR},#{email,jdbcType=VARCHAR},#{sourcePublication,jdbcType=VARCHAR},
  #{pDate,jdbcType=VARCHAR},#{reference,jdbcType=LONGVARCHAR},#{fullText,jdbcType=LONGVARCHAR},
  #{url,jdbcType=VARCHAR},#{searchKeyword,jdbcType=VARCHAR}
  )

